import React, { Component } from 'react';
import Navbar from '../common/Navbar'
import './ProfilePage.css'
import axios from 'axios'

class ProfilePage extends Component {
    state = { 
        data:{}
     }

    content = [
        {type:"link",text:"Home"},
        {type:"link",text:"AR Tour"},
        {type:"link",text:"Find Stalls"}
    ]

    data = {
        imgURL:"https://b.zmtcdn.com/data/pictures/chains/3/19485193/20568152b838846502c635dea08cbcdd.jpg?fit=around|771.75:416.25&crop=771.75:416.25;*,*",
        name:"The Burger Joint",
        items:[
            {name:"Beef Burger",imgURL:"https://www.foodrepublic.com/wp-content/uploads/2011/06/beefburger.jpg"},
            {name:"Cheese Burger",imgURL:"https://www.simplyrecipes.com/thmb/nIWS_mpUuNlVAKTdSNwm5D6JtZM=/1600x1200/smart/filters:no_upscale()/__opt__aboutcom__coeus__resources__content_migration__simply_recipes__uploads__2018__06__HT-Grill-Burger-LEAD-VERTICAL-d0ae8c04f90c4cc2b03cd0b154e439eb.jpg"},
            {name:"Chicken Burger",imgURL:"https://sifu.unileversolutions.com/image/en-LK/recipe-topvisual/2/1260-709/crispy-fried-chicken-burger-50388014.jpg"},
        ]
    }

    componentDidMount(){
        // axios.get(`/profile/${this.props.id}`)
        // .then(response => response.data)
        // .then(data => this.setState({data}))
        this.setState({data:this.data})
    }

    itemRender(){
        return this.state.data.items.map(i => {
            return (
                <div className="item-container">
                    <img className="item-image" src={i.imgURL} alt=""/>
                    <span>{i.name}</span>
                 </div>
            )
        })
    }

    render() { 
        return ( 
            <React.Fragment>
                <Navbar content={this.content}/>
                <div>
                    <div class="top-profile-page-container">
                        <div id="profile-page-image-container">
                            <span>{this.state.data.name}</span>
                            <img id="profile-top-image" src={this.state.data.imgURL} alt=""/>
                        </div>
                        <div id="chat-container">
                            <span>Chat with the owner</span>
                            <div id="chat-box">
                                chat box
                            </div>
                        </div>
                    </div>
                    <div class="bottom-profile-page-container">
                        <span>Popular Items</span>
                        <div class="profile-items">
                            {this.state.data.items && this.itemRender()}
                        </div>
                    </div>
                </div>
            </React.Fragment>
         );
    }
}
 
export default ProfilePage;
