import React,{Component} from 'react';

class LoginForm extends Component {
    state = {  }
    render() { 
        return ( 
            <div class="card-body login-body">
                <div>
                    <h5 class="card-title">Login as a {this.props.type}</h5>
                    
                </div>
                <form action={`/login/${this.props.type}`} method="POST">
                    <input type="text" id="username" name="username" className="form-control" placeholder="Enter your username"/>
                    <input type="password" id="password" name="password" className="form-control" placeholder="Enter your password"/>
                    <button type="submit" className="btn btn-primary btn-lg">Login</button>
                </form>
                <span>OR</span><br/>
                <a href={`/register/${this.props.type}`} className="btn btn-success btn-lg">Register</a><br/>
                <a href={`/login/${this.props.type}`} className="btn btn-secondary btn-lg">Not a {this.props.type}? Click Here</a>
            </div>
         );
    }
}
 
export default LoginForm;
